%%%%%%%%%%%%%%%%%%%%% % % Decorations and Conveniences % %%%%%%%%%%%%%%%%%%%%%
% 2006/09/17 Converted _, ^ to \sb, \sp for TeX4ht
% Definitions % An environment for definitions is given elsewhere. % Here we define a typeface to use when a term is defined % and route the term to the index % % Usage: \define{definition-term} % \newcommand{\define}[1]{{\bf #1}}
% In Computation Notes, we use a font % for the commands used to communicate with the device % % Usage: \computerfont{commands} % \newcommand{\computerfont}[1]{ {\tt#1} }
% Integers % % Usage: \Z \newcommand{\Z}{{{\mathbb Z}}}
% n-space over real field % % Usage: \real{integer-dimension, or \null} % \newcommand{\real}[1]{{{\mathbb R}^{#1}}}
% Complex number modulus % % Usage: \modulus{a+bi} % Presumes math mode % \newcommand{\modulus}[1]{{\left\lvert#1\right\rvert}}
% Complex number conjugation % % Usage: \conjugate{a+bi} % Presumes math mode % \newcommand{\conjugate}[1]{{\overline{#1}}}
% Standardized look for the zero vector % % Usage: \zerovector % Presumes math mode % \newcommand{\zerovector}{{\vect{0}}}
% Zero matrix % % Usage: \zeromatrix % For use in math mode, use a subscript when size is important % \newcommand{\zeromatrix}{{{\cal O}}}
% An inner product without matrix notation % % Usage: \innerproduct{a-vector}{a-vector} % Presumes math mode \newcommand{\innerproduct}[2]{{\left\langle#1,\,#2\right\rangle}}
% Norm of a vector % % Usage: \norm{a-vector} % Presumes math mode \newcommand{\norm}[1]{{\left\lVert#1\right\rVert}}
% Determinant of a matrix (functional notation) % % Usage: \detname{A} % Presumes math mode \newcommand{\detname}[1]{{\det\left(#1\right)}}
% Determinant of a matrix (vertical bars) % % Usage: \detbars{A} % Presumes math mode \newcommand{\detbars}[1]{{\left\lvert#1\right\rvert}}
% Dimension % % Usage: \dimension{vector-space-letter} % Presumes math mode % \newcommand{\dimension}[1]{{\dim\left(#1\right)}}
% Nullity % % Usage: \nullity{matrix-or-lintrans-letter} % Presumes math mode % \newcommand{\nullity}[1]{{n\left(#1\right)}}
% Rank % % Usage: \rank{matrix-or-lintrans-letter} % Presumes math mode % \newcommand{\rank}[1]{{r\left(#1\right)}}
%%%%%%%%%%%%%%%%%%%%% % % Sets % %%%%%%%%%%%%%%%%%%%%%
% A convenience for simple sets % % Usage: \set{list of element} % Presumes math mode % \newcommand{\set}[1]{{\left\{#1\right\}}}
% Sets with vertical bar, "such that", sized for objects, not condition % % Usage: \setparts{objects}{condition} % Presumes math mode % %%\newcommand{\setparts}[2]{{\left\{ #1\mid#2\right\}}} \newcommand{\setparts}[2]{{\left\{\left. #1\right\rvert#2\right\}}}
% Set Cardinality % % Usage: \card{a-set-letter} % Presumes math mode % \newcommand{\card}[1]{{\left\lvert#1\right\rvert}}
% Set Union % Use \cup
% Set Intersection % Use \cap
% Set Complement % % Usage: \setcomplement{a-set-letter} % Presumes math mode % \newcommand{\setcomplement}[1]{{\overline{#1}}}
%%%%%%%%%%%%%%%%%%%%% % % Subspace Constructions % %%%%%%%%%%%%%%%%%%%%%
% Span of a set of vectors % % \span and \sp are used by TeX for other things % % Usage: \spn{set-of-vectors} % Presumes math mode, uses ams-math \text command % % Obsoleted 2006/01/05: Sp notation \newcommand{\spn}[1]{{\left\langle#1\right\rangle}}
% Null space of a matrix % % A convenience for null spaces % % Usage: \nsp{A} % Presumes math mode % \newcommand{\nsp}[1]{{{\mathcal N}\!\left(#1\right)}}
% Column Space of a matrix % % Usage: \csp{A} % Presumes math mode % \newcommand{\csp}[1]{{\mathcal{C}\!\left(#1\right)}}
% Row Space of a matrix % % Usage: \rsp{A} % Presumes math mode % \newcommand{\rsp}[1]{{\mathcal{R}\!\left(#1\right)}}
% Left Null space of a matrix % % A convenience for null spaces % % Usage: \lns{A} % Presumes math mode % \newcommand{\lns}[1]{{\mathcal{L}\!\left(#1\right)}}
% Range of a linear transformation % % TeX uses \range for something else % % Usage: \rng{A} % Presumes math mode % \newcommand{\rng}[1]{{\mathcal{R}\!\left(#1\right)}}
% Kernel of a linear transformation % % TeX uses \ker to do something different % % Usage: \ker{A} % Presumes math mode % \newcommand{\krn}[1]{{\mathcal{K}\!\left(#1\right)}}
%%%%%%%%%%%%%%%%%%%%% % % Matrices % %%%%%%%%%%%%%%%%%%%%%
% Similarity % % Usage: \similar{inner-matrix}{outer-invertible-matrix} % Presumes math mode % Rearranging this will not "fix" all desired changes throughout % \newcommand{\similar}[2]{{\inverse{#2}#1#2}}
% Submatrix % % Usage: \submatrix{matrix-name}{delete-row}{delete-col} % Presumes math mode % \newcommand{\submatrix}[3]{{#1\left(#2|#3\right)}}
%%%%%%%%%%%%%%%%%%%%% % % Eigenvalues & Eigenspaces % %%%%%%%%%%%%%%%%%%%%%
% Characteristic polynomial % % Usage: \charpoly{matrix-letter}{variable-letter} % Presumes math mode % \newcommand{\charpoly}[2]{{p_{#1}\left(#2\right)}}
% Eigenspace % % Usage: \eigenspace{matrix-letter}{eigenvalue-letter} % Presumes math mode % \newcommand{\eigenspace}[2]{{{\mathcal E}_{#1}\left(#2\right)}}
% Generalized Eigenspace % % Usage: \geneigenspace{lin-trans-letter}{eigenvalue-letter} % Presumes math mode % \newcommand{\geneigenspace}[2]{{{\mathcal G}_{#1}\left(#2\right)}}
% Algebraic multiplicty % % Usage: \algmult{matrix-letter}{eigenvalue-letter} % Presumes math mode % \newcommand{\algmult}[2]{{\alpha_{#1}\left(#2\right)}}
% Geometric multiplicty % % Usage: \geomult{matrix-letter}{eigenvalue-letter} % Presumes math mode % \newcommand{\geomult}[2]{{\gamma_{#1}\left(#2\right)}}
% Index (of eigenvalue) % % Usage: \indx{matrix-letter}{eigenvalue-letter} % Presumes math mode % \newcommand{\indx}[2]{{\iota_{#1}\left(#2\right)}}
% Eigensystem % % Usage: \eigensystem{matrixletter}{eigenvalue}{list of basis vectors} % Presumes math mode and in align* environment % \newcommand{\eigensystem}[3]{ {\lambda&=#2&\eigenspace{#1}{#2}&=\spn{\set{#3}}} }
%%%%%%%%%%%%%%%%%%%%% % % Linear Transformations % %%%%%%%%%%%%%%%%%%%%%
% Linear transformation definition % % Usage: \ltdefn{name-letter}{domain}{range} % Presumes math mode % % Through v 2.12: % \newcommand{\ltdefn}[3]{{#1\colon #2\mapsto#3}} % Now reserved for describing the action on an individual element \newcommand{\ltdefn}[3]{{#1\colon #2\rightarrow#3}}
% Linear transformation output % % Usage: \lt{name-letter}{input} % Presumes math mode % \newcommand{\lt}[2]{{#1\left(#2\right)}}
% Linear transformation inverse % % Usage: \ltinverse{name-letter} % Presumes math mode % \newcommand{\ltinverse}[1]{{#1^{-1}}}
% Linear transformation restriction % % Usage: \restrict{name-letter}{subspace-letter} % Presumes math mode % \newcommand{\restrict}[2]{{{#1}|_{#2}}}
% Linear transformation preimage % % Usage: \preimage{name-letter}{codomain-element} % Presumes math mode % \newcommand{\preimage}[2]{{#1^{-1}\left(#2\right)}}
%%%%%%%%%%%%%%%%%%%%% % % Isomorphism and Representations % %%%%%%%%%%%%%%%%%%%%%
% Isomorphism symbol % % Usage: \isomorphic % Presumes math mode % \newcommand{\isomorphic}{{\cong}}
% Vector representation function name % % Usage: \vectrepname{basis-letter} % Presumes math mode % \newcommand{\vectrepname}[1]{{\rho_{#1}}}
% Vector representation output % % Usage: \vectrep{basis-letter}{input} % Presumes math mode % \newcommand{\vectrep}[2]{{\lt{\vectrepname{#1}}{#2}}}
% Vector representation inverse function name % (Added later, not used consistently) % % Usage: \vectrepinvname{basis-letter} % Presumes math mode % \newcommand{\vectrepinvname}[1]{{\ltinverse{\vectrepname{#1}}}}
% Vector representation inverse output % % Usage: \vectrepinv{basis-letter}{input} % Presumes math mode % \newcommand{\vectrepinv}[2]{{\lt{\ltinverse{\vectrepname{#1}}}{#2}}}
% Matrix representation % % Usage: \matrixrep{transformation-letter}{domain-basis-letter}{codomain-basis-letter} % Presumes math mode % \newcommand{\matrixrep}[3]{{M^{#1}_{#2,#3}}}
% Matrix representation column-by-colum % % Usage: \matrixrepcolumns{transformation-letter}{codomain-basis-letter}{codomain-basis-vector-letter}{final-index} % Presumes math mode % \newcommand{\matrixrepcolumns}[4]{ {\left\lbrack \left.\vectrep{#2}{\lt{#1}{\vect{#3}_{1}}}\right| \left.\vectrep{#2}{\lt{#1}{\vect{#3}_{2}}}\right| \left.\vectrep{#2}{\lt{#1}{\vect{#3}_{3}}}\right| \ldots \left|\vectrep{#2}{\lt{#1}{\vect{#3}_{#4}}}\right. \right\rbrack }}
% Change of basis % % Usage: \cbm{domain-basis-letter}{codomain-basis-letter} % Presumes math mode % \newcommand{\cbm}[2]{{C_{#1,#2}}}
% Linear transformation composition % % Usage: \compose{function-name}{function-name} % Presumes math mode % \newcommand{\compose}[2]{{{#1}\circ{#2}}}
% Vector space of linear transformations % % Usage: \vslt{domains}{codomains} % Presumes math mode % \newcommand{\vslt}[2]{{{\mathcal LT}\left(#1,\,#2\right)}}
%%%%%%%%%%%%%%%%%%%%% % % Canonical Forms % %%%%%%%%%%%%%%%%%%%%%
% Jordan blocks % % Usage: \jordan{size}{diagonal-element} % Presumes math mode % \newcommand{\jordan}[2]{{J_{#1}\left(#2\right)}}
%%%%%%%%%%%%%%%%%%%%% % % Hadamard Matrices % %%%%%%%%%%%%%%%%%%%%%
% Contributed by Elizabeth Million, to support her Topics section, HP
% Usage: \hadamard{a-matrix}{a-matrix} % Presumes math mode \newcommand{\hadamard}[2]{{#1\circ #2}}
% Hadamard identity matrix % % Usage: \hadamardidentity{paired-subscripts-size-of-matrix} \newcommand{\hadamardidentity}[1]{{J_{#1}}}
% Hadamard inverse matrix % % Usage: \hadamardinverse{matrix-expression} \newcommand{\hadamardinverse}[1]{{\widehat{#1}}}
%%%%%%%%%%%%%%%%%%%%% % % Miscellaneous % %%%%%%%%%%%%%%%%%%%%%
% Direct sum % Conveniece for actual symbol % % Usage: \ds between a couple of subspaces % \newcommand{\ds}{\oplus}
% Square Root of a Matrix % % Usage: \sr{a-matrix} % \newcommand{\sr}[1]{#1^{1/2}}
% matrix trace % % Usage: \trace{matrix name} % Persumes in math mode \newcommand{\trace}[1]{{t\left(#1\right)}}
